import { useCallback, useEffect, useState } from "react";
//import { apiMercadoLibre } from "../services";
import { getProductsXSearch } from "../helpers/getProductsXSearch";

export function useFetch({ nombre }) {
  const [data, setData] = useState();

  const fetch = useCallback(async () => {
    const response = await getProductsXSearch(nombre);
    setData(response.data);
  }, [nombre]);

  useEffect(() => {
    fetch();
  }, [fetch, nombre]);

  return { data, fetch };
}
